import React, {Component} from 'react';
import './RecipeBox.css'
import { Link } from "react-router-dom";
import { Card, Button } from 'react-bootstrap';
import { render } from '@testing-library/react';

class RecipeBox extends Component{
    constructor(props){
        super(props)
        this.state = {
            count:0
        }
    }
    increment= ()=>{
        this.setState(
            {
                count: this.state.count + 1
            }
        )
    }
    decrement = () => {
        this.setState(
            {
                count:this.state.count -1
            }
        )
    }
  
    render(){
       return (
  
            <Card className="recipe-box" style={{ width: '18rem'}}>
                <Card.Img variant="top" className="recipe-img" src={this.props.item.image} />
                <Card.Body className="recipe-content">
                    <div className="recipe-content-top">
                    <Card.Title className="recipe-title">{this.props.item.title}</Card.Title>
                    <Card.Text className="price">
                        Rs.{this.props.item.price}
                    </Card.Text>
        
                    </div>
                    <Card.Text className="description">
                        {this.props.item.description}
                    </Card.Text>

                
                   <button onclick={this.increment} className="counter">+</button>
                  <h2>{this.state.count}</h2> 
                   <button onclick={this.decrement} className="counter">-</button>


                    <Button className="add-to-cart" variant="primary">Add to Cart</Button>
                </Card.Body>
            </Card>
        
        );
        
    }
} 
export default RecipeBox;




