import React, { useState } from 'react';
import './RecipeBox.css'
import { Link } from "react-router-dom";
import { Card, Button } from 'react-bootstrap';

const RecipeBox = (props) => {
    const [num, setnum] = useState(0);
    const incrementnum = () => {
        setnum(num + 1)
    }
    const decrementnum = () => {
        setnum(num - 1)
    }
    return (

        <Card className="recipe-box" style={{ width: '18rem' }}>
            <Card.Img variant="top" className="recipe-img" src={props.item.image} />
            <Card.Body className="recipe-content">
                <div className="recipe-content-top">
                    <Card.Title className="recipe-title">{props.item.title}</Card.Title>
                    <Card.Text className="price">
                        Rs.{props.item.price}
                    </Card.Text>

                </div>
                <Card.Text className="description">
                    {props.item.description}
                </Card.Text>
                <div className="quanity-button">
                    <Button onClick={incrementnum} className="counter">in</Button>
                    <h2>{num}</h2>
                    <Button onClick={decrementnum} className="counter">de</Button>
                </div>
                <Button className="add-to-cart" variant="primary">Add to Cart</Button>
            </Card.Body>
        </Card>

    );
}

export default RecipeBox;
